export default function initSlider(){(document.querySelectorAll(".swiper_goods")||null).forEach(e=>{e&&new Swiper(e,{slidesPerView:1,spaceBetween:20,loop:!0,autoplay:!0,pagination:{el:".services_gallery_swiper .swiper-pagination",dynamicBullets:!0},breakpoints:{1366:{slidesPerView:2,spaceBetween:20}}})})}